    private void btnCriarListaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCriarListaActionPerformed
        // TODO add your handling code here:
        try{
            int tamanho = Integer.parseInt(this.fieldTamanhoLista.getText());
            if(tamanho > 0 && tamanho <=10){
                FrameListaSequencial frame = new FrameListaSequencial(tamanho);
                this.dispose();
                frame.setVisible(true);
            }
            else{
                System.out.println("erro");
            }
            
        }catch(NumberFormatException e){
            JOptionPane.showMessageDialog(null, "Digite apenas numeros", "Erro", JOptionPane.ERROR_MESSAGE);
        }            
             
    }//GEN-LAST:event_btnCriarListaActionPerformed

